class IntroText extends Component {
    render() {
        return (
            <div className="landing-text">
                <h1 className="logo text">
                    <span className='drinkingchoices'>Drink Choices</span>
                </h1>
                <h3 className="slogan text">
                    <span>Woop Woop!</span><span></span>
                    {this.props.selDrink}
