        // Issue a transaction on the number of likes to increase it by 1
        bookmarks.transaction((d) => d + 1);
    }

    bookmarkCallback(bar) {
        // this.state.barBooked = bar;
        this.props.bookmarkCallback(bar);
    }

    render() {
